import React from "react";

class GmailLogin extends React.Component {
  state = { isSignedIn: false, btnText: "Sign In" };
  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "115174029055-l95pkgcvibjc4l1j4dn08e3213214qmj.apps.googleusercontent.com",
          scope: "email",
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          if (this.auth.isSignedIn.get()) {
            this.setState({
              isSignedIn: this.auth.isSignedIn.get(),
              btnText: "Sign Out",
            });
          }

          this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
  }

  handleBtnClick = () => {
    if (this.state.isSignedIn) {
      this.auth.signOut();
    } else if (this.state.isSignedIn === false) {
      this.auth.signIn();
    }
  };
  onAuthChange = () => {
    if (this.auth.isSignedIn.get()) {
      this.setState({
        isSignedIn: this.auth.isSignedIn.get(),
        btnText: "Sign Out",
      });
    }
  };
  render() {
    return (
      <div className="socialBtn">
        <a className="button is-medium is-github" onClick={this.handleBtnClick}>
          <span className="icon">
            <i className="fab fa-google"></i>
          </span>
          <span>{this.state.btnText}</span>
        </a>
      </div>
    );
  }
}

export default GmailLogin;
