export const authReducer = (auth = { isSignedIn: false }, action) => {
  switch (action.type) {
    case "SIGN_IN":
      return { ...auth, isSignedIn: true };
    case "SIGN_OUT":
      return { ...auth, isSignedIn: false };
    default:
      return auth;
  }
};
